	(55, 7168, 8, 'Ligadura de valor e ponto de aumento', 'https://vimeo.com/243479280', '<p style="text-align:center">A seguir, vamos fazer alguns exerc&iacute;cios de leitura r&iacute;tmica que incluem ligaduras e pontos de aumento.&nbsp;</p>\r\n', NULL, NULL, '6:45', 27, 0, 0, NULL, NULL),
