	(514, 7180, 124, 'Exercício de análise harmônica', '', '<h3 style="text-align:center">Fa&ccedil;a a an&aacute;lise da harmonia da m&uacute;sica &ldquo;Maria ningu&eacute;m&rdquo; (Carlos Lyra).</h3>\r\n\r\n<p style="text-align:center">Utilize as ferramentas de escrita anal&iacute;tica (seta, colchete) onde for preciso. Indique tamb&eacute;m se h&aacute; alguma mudan&ccedil;a de tonalidade.</p>\r\n\r\n<p style="text-align:center">[audio H1-A5-maria-ninguem.mp3]</p>\r\n\r\n<p style="text-align:center"><img alt="curso online de harmonia aplicada à música popular - análise harmônica da música Maria ninguém" src="http://terradamusica.com.br/assets/imgs/aulas/H1_5_maria-ninguem.png" style="height:382px; width:900px" /></p>\r\n\r\n<p style="text-align:center">Confira a solu&ccedil;&atilde;o da an&aacute;lise no DOWNLOAD.</p>\r\n', '{"5b53ccec11aba":"H1-A5-maria-ninguem.mp3"}', '{"5b3bfd3653567":"H1-A5-solucao-Maria.pdf"}', '10:00', 3, 0, 0, NULL, NULL),
