	(516, 7180, 124, 'Mais alguns elementos de análise harmônica', '', '<h3 style="text-align:center">Os segundos cadenciais na an&aacute;lise<br />\r\n&nbsp;</h3>\r\n\r\n<p style="text-align:center">Na aula anterior, conduzimos uma primeira an&aacute;lise da m&uacute;sica &ldquo;Balan&ccedil;o zona sul&rdquo;.</p>\r\n\r\n<p style="text-align:center">Vamos observar agora novos elementos e completar&nbsp;a sua an&aacute;lise.</p>\r\n\r\n<p style="text-align:center"><img alt="curso online de harmonia aplicada à música popular - análise harmônica segundos cadenciais" src="http://terradamusica.com.br/assets/imgs/aulas/H1-A6-Analise2.png" style="height:963px; width:900px" /></p>\r\n\r\n<p style="text-align:center">[audio H1-A5-Balanco-zona-sul.mp3]</p>\r\n\r\n<p style="text-align:center">Confira a solu&ccedil;&atilde;o e outras considera&ccedil;&otilde;es no DOWNLOAD.</p>\r\n', '{"5b53cd73e4c2f":"H1-A5-Balanco-zona-sul.mp3"}', '{"5b3c0706cf3db":"H1-A6-balanco-geral.pdf"}', '4:00', 5, 0, 0, NULL, NULL),
