	(525, 7180, 125, 'Exercício 1', '', '<h3 style="text-align:center">Completar os turnarounds I7M - VIm7 - IIm7 - V7 nas tonalidades abaixo:</h3>\r\n\r\n<p style="text-align:center"><img alt="curso online de harmonia aplicada à música popular - exercício online" src="http://terradamusica.com.br/assets/imgs/aulas/H1-A7-exercicio1.png" style="height:1276px; width:1200px" /></p>\r\n\r\n<p style="text-align:center">&nbsp;</p>\r\n', NULL, '{"5b3c102b787b5":"H1-A7-Gabarito-Exercicio.pdf"}', '5:00', 14, 0, 0, NULL, NULL),
