	(527, 7180, 125, 'Exercício 2', '', '<h3>Construir as sequ&ecirc;ncias diat&ocirc;nicas IV - VII - III - VI - II - V - I no &acirc;mbito da escala maior:</h3>\r\n\r\n<p><img alt="curso online de harmonia aplicada à música popular - exercício online de harmonia" src="http://terradamusica.com.br/assets/imgs/aulas/H1-A7-exercicio2.png" style="height:1232px; width:1200px" /></p>\r\n', NULL, '{"5b3c116f75639":"H1-A7-Gabarito-Exercicio-2.pdf"}', '10:00', 16, 0, 0, NULL, NULL),
	(528, 7180, 125, 'Exercício de análise harmônica', '', '<h3 style="text-align:center">Analise a harmonia da m&uacute;sica&nbsp;<em>Como dois e dois</em>, de Caetano Veloso:</h3>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<h3 style="text-align:center"><img alt="Análise harmônica da música Como dois e dois - Caetano Veloso - Curso online de harmonia" src="http://terradamusica.com.br/assets/imgs/aulas/H1-A7_Como-dois-e-dois.png" style="height:695px; width:900px" /></h3>\r\n', NULL, '{"5b3d05ca16c0c":"H1-A7-Como-dois-e-dois.pdf"}', '10:00', 17, 0, 0, NULL, NULL),
