	(708, 7182, 147, 'Exercício de análise harmônico-funcional', '', '<p style="text-align:center">Conduza a an&aacute;lise harm&ocirc;nico-funcional da m&uacute;sica &ldquo;Se todos fossem iguais a voc&ecirc;&rdquo; (Tom Jobim e Vinicius de Moraes).</p>\r\n\r\n<h2 style="text-align:center"><span style="color:#cc0000">&ldquo;Se todos fossem iguais a voc&ecirc;&rdquo;&nbsp;</span></h2>\r\n\r\n<p style="text-align:center"><img alt="curso online avançado de harmonia popular - terra da música" src="http://terradamusica.com.br/assets/imgs/aulas/Se-Todos-fossem-iguais-a-voce.png" style="height:891px; width:900px" /></p>\r\n\r\n<p style="text-align:center">&nbsp;</p>\r\n\r\n<h4 style="text-align:center">Envie o seu trabalho de an&aacute;lise para mim (turi@terradamusica.com.br)</h4>\r\n', NULL, NULL, '10:00', 29, 0, 0, NULL, NULL),
