	(759, 7182, 155, 'Exercício de análise musical', 'https://vimeo.com/186357021', '<p style="text-align:center">Vamos&nbsp;analisar a&nbsp;harmonia da m&uacute;sica &ldquo;Fotografia&rdquo; de Tom Jobim.&nbsp;A partitura utilizada aqui para a an&aacute;lise prov&eacute;m do Songbook de Tom Jobim&nbsp;(Editora Lumiar).</p>\r\n\r\n<p style="text-align:center">Fa&ccedil;a a an&aacute;lise harm&ocirc;nica e indique as escalas que podem ser usadas sobre cada um dos acordes.</p>\r\n\r\n<h2 style="text-align:center"><span style="color:#cc0000">&ldquo;Fotografia&rdquo;</span></h2>\r\n\r\n<p style="text-align:center"><img alt="análise da bossa nova - curso online avançado de harmonia popular - terra da música" src="http://terradamusica.com.br/assets/imgs/aulas/Fotografia.png" style="height:780px; width:1000px" /></p>\r\n', NULL, NULL, '15:00', 32, 0, 0, NULL, NULL),
