	(769, 7183, 156, 'Exercício de fixação na tonalidade maior', '', '<p style="text-align:center">Seguindo o exemplo contido no t&oacute;pico anterior, construir o mapa das regi&otilde;es das tonalidades vizinhas de R&eacute; maior:</p>\r\n\r\n<p style="text-align:center"><img alt="Tonalidades vizinhas - exercícios de harmonia - curso online prof. turi collura terradamusica" src="http://terradamusica.com.br/assets/imgs/aulas/Regioes-Exerc-maior.png" style="height:1098px; width:1200px" /></p>\r\n', NULL, '{"5b43edfa2efaf":"H4-A1-mapa-das-regioes-Re.pdf"}', '5:00', 2, 0, 0, NULL, NULL),
