	(772, 7183, 156, 'Exercício de fixação', '', '<h3 style="text-align:center">Determinar&nbsp;as tonalidades secund&aacute;rias que abastecem as tonalidades indicadas:</h3>\r\n\r\n<p style="text-align:center">&nbsp;</p>\r\n\r\n<p style="text-align:center"><img alt="Afinidade de terça entre tonalidades - curso online avançado de harmonia popular - terra da música" src="http://terradamusica.com.br/assets/imgs/aulas/afinidades-de-terca.png" style="height:1319px; width:1200px" /></p>\r\n', NULL, '{"5b43eee3d4fc6":"H4-A1-exercicio-afinidades-de-terca.pdf"}', '5:00', 5, 0, 0, '', 'Exercício de fixação'),
