	(774, 7183, 156, 'Exercício de análise: Inútil paisagem', 'https://vimeo.com/183303569', '<p style="text-align:center">Fa&ccedil;a a an&aacute;lise da harmonia da m&uacute;sica&nbsp;<em>In&uacute;til paisagem</em>.&nbsp;</p>\r\n\r\n<h2 style="text-align:center"><span style="color:#cc0000">&ldquo;In&uacute;til paisagem&rdquo; (Tom Jobim)</span></h2>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p style="text-align:center"><img alt="análise de Inutil Paisagem - curso online avançado de harmonia popular - terra da música" src="http://terradamusica.com.br/assets/imgs/aulas/inutil-paisagem.png" style="height:833px; width:1200px" /></p>\r\n', NULL, NULL, '15:00', 7, 0, 0, NULL, NULL),
