	(876, 7183, 165, 'Exercício de fixação: ES nas escalas S-T', '', '<h4 style="text-align:center">Indicar as tr&iacute;ades maiores contidas nas escalas S-T abaixo indicada:</h4>\r\n\r\n<p style="text-align:center"><img alt="exercícios de harmonia - escala semitom-tom - terra da música prof. Turi Collura" src="http://terradamusica.com.br/assets/imgs/aulas/H3-A11-Exercicio-ES-na-S-T.png" style="height:337px; width:1100px" /></p>\r\n', NULL, '{"5b491fea7c37c":"Solucao-Exercicio-triades-S-T.pdf"}', '6:00', 30, 0, 0, NULL, NULL),
