	(1783, 20522, 510, 'Exercício interativo 7', '', '<h3 style="text-align:center">Identifique as notas indicadas, na clave de F&aacute; (toda a extens&atilde;o do pentagrama e acidentes):</h3>\r\n\r\n<p style="text-align:center">&nbsp;</p>\r\n\r\n<p style="text-align:center"><iframe frameborder="0" height="650" src="https://www.musictheory.net/exercises/note/nrty9yybnyyyyy" width="600"></iframe></p>\r\n', NULL, NULL, '10:00', 14, 0, 0, '', ''),
	(1784, 20522, 510, 'Treinando a leitura no pentagrama', '', '<h3 style="text-align:center">Vamos fazer um exerc&iacute;cio que resume tudo que estudamos at&eacute; aqui.</h3>\r\n\r\n<h3 style="text-align:center">Experimente adquirir certa rapidez no reconhecimento das notas nas duas claves:</h3>\r\n\r\n<p style="text-align:center"><iframe frameborder="0" height="650" src="https://www.musictheory.net/exercises/note/oyyay9yybnyyyyy" width="600"></iframe></p>\r\n', NULL, NULL, '10:00', 15, 0, 0, '', ''),
