	(1995, 20529, 637, 'Introdução', 'https://vimeo.com/160519203', '<h4 style="text-align:center">O que estudamos nesta aula:</h4>\r\n\r\n<h4 style="text-align:center"><img alt="Terra da música - curso online de harmonia popular prof. Turi Collura" src="http://terradamusica.com.br/assets/imgs/aulas/IMG_TOPICOS-Aula-1.png" style="height:463px; width:700px" /></h4>\r\n', NULL, NULL, '1:36', 0, 0, 0, '', ''),
	(1996, 20529, 637, 'As escalas pentatônicas', 'https://vimeo.com/159426482', '<h2 style="text-align:center"><span style="color:#bf0808">Exerc&iacute;cio</span></h2>\r\n\r\n<h4 style="text-align:center">Vamos fazer um r&aacute;pido exerc&iacute;cio: escreva as escalas pentat&ocirc;nicas menores pedidas e, logo depois, sua escala pentat&ocirc;nica maior relativa:</h4>\r\n\r\n<p style="text-align:center"><img alt="Escalas pentatônicas exercício - curso online de harmonia e improvisação - terra da música" src="http://terradamusica.com.br/assets/imgs/aulas/Exercicio-penta-1.png" style="height:305px; width:900px" /></p>\r\n', NULL, '{"5e1db8513d23c":"H2-A1-As-Escalas-Pentatonicas.pdf"}', '5:00', 0, 0, 0, '', ''),
